/* plantilla de los informes */CREATE TABLE templates (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  name VARCHAR(255) NOT NULL UNIQUE);/* secciones de la plantilla */CREATE TABLE t_sections (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  name VARCHAR(255) NOT NULL);/* subsecciones de la seccion de la plantilla */CREATE TABLE t_subsections (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  name VARCHAR(255) NOT NULL);/* grupos de la subseccion de la seccion de la plantilla */CREATE TABLE t_groups (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  name VARCHAR(255) NOT NULL);/* campos del grupo de la subseccion de la seccion de la plantilla */CREATE TABLE t_fields (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  name VARCHAR(255) NOT NULL,  field_type VARCHAR(255) NOT NULL);CREATE TABLE users_templates (  id_user UUID NOT NULL,  id_template UUID NOT NULL,  id_project UUID NOT NULL);CREATE TABLE t_templates_sections (  id_template UUID NOT NULL,  id_section UUID NOT NULL);CREATE TABLE t_sections_subsections (  id_section UUID NOT NULL,  id_subsection UUID NOT NULL);CREATE TABLE t_subsections_groups (  id_subsection UUID NOT NULL,  id_group UUID NOT NULL);CREATE TABLE t_groups_fields (  id_group UUID NOT NULL,  id_field UUID NOT NULL);