CREATE TABLE reports (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  id_user UUID NOT NULL,  id_project UUID NOT NULL,  id_template INT NOT NULL,  title VARCHAR(255) NOT NULL,  sent BOOLEAN NOT NULL DEFAULT FALSE,  created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,  updated_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP);CREATE TABLE fields (  id_report UUID NOT NULL,  item int NOT NULL,  name VARCHAR(255) NOT NULL,  value TEXT,  PRIMARY KEY (id_report, item));