CREATE TABLE reports (  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  id_user UUID NOT NULL,  id_project UUID NOT NULL,  id_template INT NOT NULL,  title VARCHAR(255) NOT NULL,  created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,  updated_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP);CREATE TABLE sections(  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  id_report UUID NOT NULL,  name VARCHAR(255) NOT NULL);CREATE TABLE subsections(  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  id_section UUID NOT NULL,  name VARCHAR(255) NOT NULL);CREATE TABLE groups(  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  id_subsection UUID NOT NULL,  name VARCHAR(255) NOT NULL,  comment VARCHAR(255) NOT NULL);CREATE TABLE fields(  id UUID PRIMARY KEY NOT NULL DEFAULT uuid_generate_v4(),  id_group UUID NOT NULL,  name VARCHAR(255) NOT NULL,  value TEXT);